import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import {Redirect} from 'react-router-dom';


class RecipeCard extends React.Component{
    constructor(props){
        super(props);
        this.title = props.title;
        this.prep = props.prep;
        this.id = props.id;
        this.state={
            navRecipe:false,
        }
        this.goToRecipe = this.goToRecipe.bind(this);
    }

    //view recipe btn should take the user to the singleRecipe page
    goToRecipe(){
        //set it ready to navigate to specific recipe page
        console.log(this.props.id);
        this.setState({
            navRecipe: true,
        })
    }



    render(){
        if(this.state.navRecipe){
            var link = "/recipe/view/"+this.state.id
            return <Redirect to={link} />
        }


        return(
        <Card bg="light" style={{width: '18rem'}}>
            <Card.Body>
                <Card.Img variant="top" src=""/>
                <Card.Title>{this.title}</Card.Title>
                <Card.Text>{this.prep}</Card.Text>
                <Button onClick={this.goToRecipe}>View Recipe</Button>
            </Card.Body>
        </Card>

        )
    }

}

export default RecipeCard;
