import { ADD_RECIPE, DELETE_RECIPE, EDIT_RECIPE, LOGIN, LOGOUT, DELETE_ACCOUNT} from './actionType';
import axios from 'axios';


export const addRecipe = (recipe) => ({
    type: ADD_RECIPE,
    recipe: recipe
})

export const deleteRecipe = (index) => ({
    type: DELETE_RECIPE,
    index: index
})

export const editRecipe = (recipe, index) => ({
    type: EDIT_RECIPE,
    recipe:recipe,
    index: index
})




export function login(email, password){
    console.log('we are starting');
    return async(dispatch)=>{
    try {
        const res = await axios.post('/login', 
        {
            "email": email,
            "password": password
            
        },
        {
        headers: {
            'Content-Type': 'application/json'
          }
        }   
        );

        console.log('out of res');

        dispatch({
            type: LOGIN,
            payload: res.data
        });
    } 
    catch (error) {
        console.log('error caught in action.js');
    }

    /*{
    type: LOGIN,
    email: email,
    password: password
}*/}
}

export const logout = () => ({
    type: LOGOUT
})




export const deleteAccount = () => ({
    type: DELETE_ACCOUNT
})
