import axios from "axios";

function setAuth(token){
  if (token) {
    axios.defaults.headers.common = {'Authorization': `bearer `+token};
    console.log('added the token');
    return true
  } else {
    delete axios.defaults.headers.common;
    return false
  }
};

export default setAuth;
