import { ADD_RECIPE, DELETE_RECIPE, EDIT_RECIPE, LOGIN, LOGOUT, DELETE_ACCOUNT, LOAD_USER} from './actionType';
import axios from 'axios';
import setAuth from '../utils/setAuth.js';


export const addRecipe = (recipe) => ({
    type: ADD_RECIPE,
    recipe: recipe
})

export const deleteRecipe = (index) => ({
    type: DELETE_RECIPE,
    index: index
})

export const editRecipe = (recipe, index) => ({
    type: EDIT_RECIPE,
    recipe:recipe,
    index: index
})

function auth(){
    if(localStorage.token){
        setAuth(localStorage.token);
    }
}


const userInfo = () => {
    //gets the user info from db
    //call the api to get user info
    return (dispatch)=>{
            console.log('we are getting the user info rn');
                axios.get('/login', 
                {headers: {
                    'Content-Type': 'application/json'}
                }).then(res=>{
                    dispatch({
                        type: LOAD_USER,
                        payload: res.data
                    });
                })
                .catch(err=>
                    console.log(err));
                
                //dispatch load user to set user info
            } 
}



export function login(email, password){
    console.log('we are starting');
    return async(dispatch)=>{
    try {
        const res = await axios.post('/login', 
        {
            "email": email,
            "password": password
            
        },
        {
        headers: {
            'Content-Type': 'application/json'
          }
        }   
        );

        dispatch({
            type: LOGIN,
            payload: res.data
        });

        //load the user info
        console.log('getting user info over here dud');
        auth();
        userInfo();
    } 
    catch (error) {
        console.log('error caught in action.js');
    }

    /*{
    type: LOGIN,
    email: email,
    password: password
}*/}
}

export const logout = () => ({
    type: LOGOUT
})




export const deleteAccount = () => ({
    type: DELETE_ACCOUNT
})
