import { ADD_RECIPE, DELETE_RECIPE, EDIT_RECIPE, LOGIN, LOGOUT, DELETE_ACCOUNT} from './actionType';
import React, {useReducer} from "react";
import axios from 'axios';
import recipes from './reducers/index.js';


export const addRecipe = (recipe) => ({
    type: ADD_RECIPE,
    recipe: recipe
})

export const deleteRecipe = (index) => ({
    type: DELETE_RECIPE,
    index: index
})

export const editRecipe = (recipe, index) => ({
    type: EDIT_RECIPE,
    recipe:recipe,
    index: index
})




export function login(email, password){
    return async(dispatch)=>{
    try {
        const res = await axios.get('/users', {
            headers: {
                'Content-Type': 'application/json'
            },
        body: {
            email: email,
            password: password
        }})

        dispatch({
            type: LOGIN,
            payload: res.data
        })
    } 
    catch (error) {
        console.log('error caught in action.js');
    }

    /*{
    type: LOGIN,
    email: email,
    password: password
}*/}
}

export const logout = () => ({
    type: LOGOUT
})




export const deleteAccount = () => ({
    type: DELETE_ACCOUNT
})
