import React from 'react';
import {connect} from 'react-redux';
import {addRecipe, deleteRecipe, editRecipe, userInfo} from '../redux/actions';
import 'bootstrap/dist/css/bootstrap.css';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import {Redirect} from 'react-router-dom';
import {Holder} from './holder.svg';


//Recipe Form can display edit mode and create a new recipe

class RecipeForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            recipe: props.recipe,
            name: props.recipe.name || "",
            prep: props.recipe.prep_time || "",
            cook: props.recipe.cook_time || "",
            ingredients: props.recipe.ingredients || "",
            directions: props.recipe.directions || "",
            image: props.recipe.image ||"",
            file: "",
    
            newRecipe: props.newRecipe,
            showModal: false,
            index: props.index,

            navAllRecipe:false
        }
        this.handleInputChange = this.handleInputChange.bind(this);
        this.mySubmitHandler = this.mySubmitHandler.bind(this);
        this.addRecipe = this.addRecipe.bind(this);
        this.editRecipe = this.editRecipe.bind(this);
        this.deleteRecipe = this.deleteRecipe.bind(this);
        this.showModal = this.showModal.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.backToAllRecipe = this.backToAllRecipe.bind(this);
        this.previewImage = this.previewImage.bind(this);
        this.upload = this.upload.bind(this);
    }

    async componentDidMount(){
        await this.props.userInfo()
    }

    handleInputChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    mySubmitHandler = (event) => {
        console.log('addRecipe has been clicked');

        event.preventDefault();
        /*var name = document.forms["recipeForm"]["name"].value;
        var prep = document.forms["recipeForm"]["prep"].value;
        var cook = document.forms["recipeForm"]["cook"].value;
        var ingredients = document.forms["recipeForm"]["ingredients"].value;
        var directions = document.forms["recipeForm"]["directions"].value;
        var img = this.state.image;

        if(name==="" || prep==="" || cook==="" || ingredients==="" || directions==="" ){
            alert("Field cannot be empty");
        }
        */
      //  else{

        const data = new FormData();

        var name = document.forms["recipeForm"]["name"].value;
        var prep = document.forms["recipeForm"]["prep"].value;
        var cook = document.forms["recipeForm"]["cook"].value;
        var ingredients = document.forms["recipeForm"]["ingredients"].value;
        var directions = document.forms["recipeForm"]["directions"].value;
        var image = document.forms["recipeForm"]["image"].value;

        console.log(this.state.file);

        var newRecipe= {
            name: name,
            prep_time: prep,
            cook_time: cook,
            ingredients: ingredients.split("\n"),
            directions: directions
        }

        data.append('recipe', newRecipe);
        data.append('image', image);

        for(var pair of data.entries()) {
            console.log(pair[0]+ ', '+ pair[1]); 
         }
        /*    var newRecipe= {
                name: name,
                prep_time: prep,
                cook_time: cook,
                ingredients: ingredients.split("\n"),
                directions: directions,
                image: this.state.file
            }
            if(this.state.newRecipe){
                console.log('making new recipe');
                this.addRecipe(newRecipe); //if new call add recipe
            }
            else{
                console.log('making edits to a recipe');
                this.editRecipe(newRecipe, this.state.index); //else call editRecipe dispatch
            }
        //}*/
      }


    async addRecipe(recipe){
        //adds the recipe and then redirects to all recipes
        console.log('addRecipe has been clicked');
        await this.props.addRecipe(recipe);
        if(this.props.added){//check for an error instead (if successful, or token expired attempt back to all recipe)
            //if server error [show the error] on the page so enable it
            this.backToAllRecipe()
        }
    }

    async editRecipe(recipe, index){
        //edits the recipe and then redirects to all recipes
        console.log('addRecipe has been clicked');
        await this.props.editRecipe(recipe,index);
        if(this.props.added){
            this.backToAllRecipe()
        }
    }

    async deleteRecipe(){
        console.log(this.state.recipe._id);
        await this.props.deleteRecipe(this.state.recipe._id, this.state.index); //pass entry id
        //call the delete api call here
        this.handleClose();
        //navigate to viewallrecipes
        this.backToAllRecipe();

    }

    showModal(){
        this.setState({
            showModal:true
        })
    }
  
    handleClose(){
        this.setState({
            showModal: false
        })
    }

    backToAllRecipe(){
        this.setState({
            navAllRecipe:true
        })
    }


    //upload image
    previewImage(event){
        //allows the user to submit an image from their files
        console.log(event.target.files[0]);
        this.setState({
            image: URL.createObjectURL(event.target.files[0]),
            file: event.target.files[0]
          })
    }


    //changes image to base64 string
    upload(file){
        let data = new FormData();
        data.append('RecipeImage', file);
        return data
      };



    render(){
        if(this.state.navAllRecipe){
            return <Redirect to="/viewRecipes"/>
        }
        if(!this.props.loggedIn){
            return <Redirect to="/landing"/>
        }
        var buttons;
        var title;
        if(this.state.newRecipe){
            buttons = <Button type="submit" className="btn-bblue">Add Recipe</Button>;
            title = "Add a Recipe";
        }
        else{
            buttons = <Button type="submit" className="btn-bblue">Save</Button>;
            var deleteBtn = <Button className="btn-delete" variant="danger" onClick={this.showModal}>Delete</Button>;
            title = "Editing a Recipe";

        }

        var img;
        if(this.state.image === ""){
            img =<img src={require('../images/no-image-available.png')} className="recipe-image" id="practice" alt="No file found"/>;

        }

        return(
        <div className="form editRecipe">
            <Row className="spacing">
                <Col>
                <Button className="btn-bblue" onClick={this.backToAllRecipe}>Back To Recipe</Button>
                </Col>
                <Col className="right">
                {deleteBtn}
                </Col>
            </Row>

            <h1>{title}</h1>


            <Form name="recipeForm" onSubmit={this.mySubmitHandler} encType="multipart/form-data">
            <Form.Group as={Row} className="left-text spacing">
                <Form.Label column sm="2" className="title">Recipe Name</Form.Label>
                <Col sm="10">
                <Form.Control type="text" name="name" value={this.state.name} onChange={this.handleInputChange}></Form.Control>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="left-text spacing">
                <Form.Label column sm="12" className="title">Image Upload</Form.Label>
            </Form.Group>
            <Form.Group as={Row} className="left-text">
                <Form.File name="image" id="recipe_image" label="Upload Image" accept="image/*" onChange={this.previewImage}>
                </Form.File>
                <img src={this.state.image} className="recipe-image" id="preview" alt="No file found"/>
                {img}
            </Form.Group>

            <Form.Row className="left-text spacing">
                <Col>
                    <Form.Group as={Row}>
                        <Form.Label column sm="4" className="title">Prep Time:</Form.Label>
                        <Col sm="7">
                        <Form.Control name="prep" type="text" value={this.state.prep} onChange={this.handleInputChange}></Form.Control> 
                        </Col>
                    </Form.Group>
                </Col>

                <Col>
                    <Form.Group as={Row}>
                        <Form.Label column sm="4" className="title">Cook Time:</Form.Label>
                        <Col sm="7">
                        <Form.Control name="cook" type="text" value={this.state.cook} onChange={this.handleInputChange}></Form.Control>
                        </Col>
                    </Form.Group>
                </Col>
            </Form.Row>

            <Form.Group className="left-text spacing">
            <Form.Label className="title">Ingredients: </Form.Label>
                <Form.Control name="ingredients" as="textarea" rows="4" value={this.state.ingredients} onChange={this.handleInputChange}></Form.Control>
            </Form.Group>

            <Form.Group className="left-text spacing">
            <Form.Label className="title">Directions</Form.Label>
                <Form.Control name="directions" as="textarea" rows="4" value={this.state.directions} onChange={this.handleInputChange}></Form.Control>
            </Form.Group>

            {buttons}
            </Form>


            <Modal
        show={this.state.showModal}
        onHide={this.handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Delete Recipe</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this recipe?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={this.handleClose}>
            No
          </Button>
          <Button variant="primary" onClick={this.deleteRecipe}>Yes</Button>
        </Modal.Footer>
      </Modal>



        </div>

        )
    }

}

//connect redux dispatch here
const mapDispatchToProps = {
    addRecipe,
    deleteRecipe,
    editRecipe,
    userInfo
};

const mapStateToProps = state => ({
    added: state.added,
    loggedIn: state.logged_in
})

export default connect(mapStateToProps,mapDispatchToProps)(RecipeForm);
